INSERT INTO `wp_posts` VALUES (84,1,'2014-12-11 12:00:40','2014-12-11 10:00:40','The Production Management Simulator is a virtual plant providing a window into the production system. This integrated application combines both flow assurance and topside process models.\r\n\r\nUsed in parallel with plant operation, this tool allows operations personnel to anticipate upcoming production events and refine operating strategies.\r\n\r\nFor additional information please visit <a href=\"http://www.simulationrsi.com/spip.php?article3\">http://www.simulationrsi.com/spip.php?article3</a>','Production Management Simulator','','publish','closed','closed','','production-management-simulator','','','2014-12-24 11:00:51','2014-12-24 09:00:51','',37,'http://localhost:8000/?page_id=84',3,'page','',0);
